import React, { useState } from 'react';
import ShowReelForm from './components/ShowReelForm';
import ClipList from './components/ClipList';
import ClipForm from './components/ClipForm';

const App = () => {
  const [showReel, setShowReel] = useState(null);
  const [clips, setClips] = useState([]);

  const handleShowReelCreated = (newShowReel) => {
    setShowReel(newShowReel);
    setClips([]);
  };

  const handleClipAdded = (newClip) => {
    setClips([...clips, newClip]);
  };

  const handleClipRemove = (clipId) => {
    setClips(clips.filter(clip => clip.id !== clipId));
  };

  return (
    <div>
      <h1>Show Reel Application</h1>
      <ShowReelForm onShowReelCreated={handleShowReelCreated} />
      {showReel && (
        <div>
          <h2>Show Reel: {showReel.name}</h2>
          <ClipForm showReelId={showReel.id} onClipAdded={handleClipAdded} />
          <ClipList clips={clips} onClipRemove={handleClipRemove} />
        </div>
      )}
    </div>
  );
};

export default App;
