import React, { useState } from 'react';
import axios from 'axios';
import { Form, Button, Container, Row, Col } from 'react-bootstrap';

const ShowReelForm = ({ onShowReelCreated }) => {
  const [name, setName] = useState('');
  const [videoStandard, setVideoStandard] = useState('PAL');
  const [videoDefinition, setVideoDefinition] = useState('HD');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/show_reels', { name, videoStandard, videoDefinition });
      onShowReelCreated(response.data);
    } catch (error) {
      console.error("There was an error creating the show reel!", error);
    }
  };

  return (
    <Container className="mt-5">
      <Row className="justify-content-md-center">
        <Col md={6}>
          <h1 className="mb-4">Create Show Reel</h1>
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="formName" className="mb-3">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </Form.Group>
            <Form.Group controlId="formVideoStandard" className="mb-3">
              <Form.Label>Video Standard</Form.Label>
              <Form.Control
                as="select"
                value={videoStandard}
                onChange={(e) => setVideoStandard(e.target.value)}
                required
              >
                <option value="PAL">PAL</option>
                <option value="NTSC">NTSC</option>
              </Form.Control>
            </Form.Group>
            <Form.Group controlId="formVideoDefinition" className="mb-3">
              <Form.Label>Video Definition</Form.Label>
              <Form.Control
                as="select"
                value={videoDefinition}
                onChange={(e) => setVideoDefinition(e.target.value)}
                required
              >
                <option value="SD">SD</option>
                <option value="HD">HD</option>
              </Form.Control>
            </Form.Group>
            <Button variant="primary" type="submit">
              Create Show Reel
            </Button>
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default ShowReelForm;
