import React, { useState } from 'react';
import axios from 'axios';

const VideoClipList = ({ showReel, clips }) => {

  let updatedClips = clips;
  const [videoClips, setVideoClips] = useState([]);

  // No need for useEffect to fetch clips if they are passed as props



  const handleRemoveClip = async (clipId) => {
    try {
      await axios.delete(`/show_reels/${showReel.id}/clips/${clipId}`);
      // Remove the clip from the 'clips' array passed as props
      updatedClips = updatedClips.filter(updatedClip => updatedClip.id !== clipId);
      setVideoClips(updatedClips); // Update state to reflect the removal
    } catch (error) {
      console.error('Error removing video clip:', error);
    }
  };



  return (
    <div>
      {updatedClips.length > 0  && (
        <div>
          <h3>{showReel.name} - Video Clips</h3>
          <ul>
            {updatedClips.map((updatedClips) => ( // Use the 'clips' prop directly here
              <li key={updatedClips.id}>
                {updatedClips.name} ({updatedClips.start_timecode} - {updatedClips.end_timecode})
                <button onClick={() => handleRemoveClip(updatedClips.id)}>Remove</button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default VideoClipList;
