import React, { useState } from 'react';
import ShowReelForm from './components/ShowReelForm';
import ClipList from './components/VideoClipList';
import ClipForm from './components/VideoClipForm';

const App = () => {
  const [showReel, setShowReel] = useState(null);
  const [clips, setClips] = useState([]);

  const handleShowReelCreated = (newShowReel) => {
    setShowReel(newShowReel);
    setClips([]);
  };

  const handleClipAdded = (newClip) => {
    setClips([...clips, newClip]);
  };

  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <div className="container">
          <a className="navbar-brand" href="/">Show Reel Application</a>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav">
              <li className="nav-item">
                <a className="nav-link" href="/show_reels">Show All Reels</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="/create_reel">Create Reel</a>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      <div className="container mt-4">
        <h1>Show Reel Application</h1>
        <ShowReelForm onCreate={handleShowReelCreated} />
        {showReel && (
          <div>
            <h2>Show Reel: {showReel.name}</h2>
            <ClipForm showReelId={showReel.id} onAddClip={handleClipAdded} />
            <ClipList showReel={showReel} clips={clips} />
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
