import React, { useState, useEffect } from 'react';
import axios from 'axios';
import VideoClipList from './VideoClipList';
import { Link } from 'react-router-dom'; // Import Link for navigation

const ShowReelList = () => {
  const [showReels, setShowReels] = useState([]);
  const [selectedShowReel, setSelectedShowReel] = useState(null);
  const [associatedClips, setAssociatedClips] = useState([]);

  useEffect(() => {
    fetchShowReels();
  }, []);

  const fetchShowReels = async () => {
    try {
      const response = await axios.get('/show_reels');
      setShowReels(response.data);
    } catch (error) {
      console.error('Error fetching show reels:', error);
    }
  };

  const fetchAssociatedClips = async (showReelId) => {
    try {
      console.log(showReelId);
      const response = await axios.get(`/show_reels/${showReelId}/clips`);
      console.log(response.data);
      setAssociatedClips(response.data);
    } catch (error) {
      console.error('Error fetching associated clips:', error);
    }
  };

  const handleSelectShowReel = (showReel) => {
    setSelectedShowReel(showReel);
    fetchAssociatedClips(showReel.id);
  };

  return (
    <div className="container py-4">
      <h2 className="mb-4">Show Reels</h2>
      <div className="list-group mb-4">
        {showReels.map((showReel) => (
          <div key={showReel.id}>
            <button
              className={`list-group-item list-group-item-action ${selectedShowReel === showReel ? 'active' : ''}`}
              onClick={() => handleSelectShowReel(showReel)}
            >
              {showReel.name}
            </button>
            {selectedShowReel === showReel && (
              <div className="dropdown mb-3">
                <button className="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                  Associated Clips
                </button>
                <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                  {associatedClips.length === 0 ? (
                    <button className="dropdown-item" type="button">No clips available</button>
                  ) : (
                    associatedClips.map((clip) => (
                      <div key={clip.id}>
                        <button className="dropdown-item" type="button">{clip.name}</button>
                        <button className="btn btn-danger btn-sm" type="button">Remove</button>
                      </div>
                    ))
                  )}
                  <Link to="/create_clip" className="dropdown-item">Create Clip</Link>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ShowReelList;
