import React, { useState } from 'react';
import axios from 'axios';

const VideoClipForm = ({ showReelId, onAddClip }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [startTimecode, setStartTimecode] = useState('');
  const [endTimecode, setEndTimecode] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await axios.post(`/api/v1/show_reels/${showReelId}/video_clips`, {
      video_clip: {
        name,
        description,
        start_timecode: startTimecode,
        end_timecode: endTimecode,
      },
    });
    onAddClip(response.data);
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
        placeholder="Clip Name"
        required
      />
      <input
        type="text"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        placeholder="Description"
      />
      <input
        type="text"
        value={startTimecode}
        onChange={(e) => setStartTimecode(e.target.value)}
        placeholder="Start Timecode"
        required
      />
      <input
        type="text"
        value={endTimecode}
        onChange={(e) => setEndTimecode(e.target.value)}
        placeholder="End Timecode"
        required
      />
      <button type="submit">Add Clip</button>
    </form>
  );
};

export default VideoClipForm;
