import React, { useState } from 'react';
import axios from 'axios';

const VideoClipList = ({ showReel, clips }) => {
  const [videoClips, setVideoClips] = useState([]);

  // No need for useEffect to fetch clips if they are passed as props

  const handleAddClip = (newClip) => {
    setVideoClips([...videoClips, newClip]);
  };

  const handleRemoveClip = async (clipId) => {
    try {
      await axios.delete(`/show_reels/${showReel.id}/clips/${clipId}`);
      setVideoClips(videoClips.filter(clip => clip.id !== clipId));
    } catch (error) {
      console.error('Error removing video clip:', error);
    }
  };

  return (
    <div>
      {showReel && showReel.clips && showReel.clips.length > 0  && (
        <div>
          <h3>{showReel.name} - Video Clips</h3>
          <VideoClipForm showReelId={showReel.id} onAddClip={handleAddClip} />
          <ul>
            {clips.map((clip) => ( // Use the 'clips' prop directly here
              <li key={clip.id}>
                {clip.name} ({clip.start_timecode} - {clip.end_timecode})
                <button onClick={() => handleRemoveClip(clip.id)}>Remove</button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default VideoClipList;
