import React from 'react';

const ClipList = ({ clips, onClipRemove }) => {
  const totalDuration = clips.reduce((acc, clip) => {
    const [startHours, startMinutes, startSeconds, startFrames] = clip.start_timecode.split(':').map(Number);
    const [endHours, endMinutes, endSeconds, endFrames] = clip.end_timecode.split(':').map(Number);

    const startTimeInSeconds = startHours * 3600 + startMinutes * 60 + startSeconds + startFrames / 25;
    const endTimeInSeconds = endHours * 3600 + endMinutes * 60 + endSeconds + endFrames / 25;

    return acc + (endTimeInSeconds - startTimeInSeconds);
  }, 0);

  const formatDuration = (seconds) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = Math.floor(seconds % 60);
    const frames = Math.round((seconds % 1) * 25);
    return `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}:${frames.toString().padStart(2, '0')}`;
  };

  return (
    <div>
      <h2>Clips</h2>
      <ul>
        {clips.map((clip, index) => (
          <li key={index}>
            {clip.name} ({clip.start_timecode} - {clip.end_timecode})
            <button onClick={() => onClipRemove(clip.id)}>Remove</button>
          </li>
        ))}
      </ul>
      <div>Total Duration: {formatDuration(totalDuration)}</div>
    </div>
  );
};

export default ClipList;
