import React, { useState } from 'react';
import axios from 'axios';

const ClipForm = ({ showReelId, onClipAdded }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [videoStandard, setVideoStandard] = useState('PAL');
  const [videoDefinition, setVideoDefinition] = useState('HD');
  const [startTimecode, setStartTimecode] = useState('');
  const [endTimecode, setEndTimecode] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`/show_reels/${showReelId}/clips`, {
        name,
        description,
        video_standard: videoStandard,
        video_definition: videoDefinition,
        start_timecode: startTimecode,
        end_timecode: endTimecode
      });
      onClipAdded(response.data);
    } catch (error) {
      console.error("There was an error adding the clip!", error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Name</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
      </div>
      <div>
        <label>Description</label>
        <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} />
      </div>
      <div>
        <label>Video Standard</label>
        <select value={videoStandard} onChange={(e) => setVideoStandard(e.target.value)} required>
          <option value="PAL">PAL</option>
          <option value="NTSC">NTSC</option>
        </select>
      </div>
      <div>
        <label>Video Definition</label>
        <select value={videoDefinition} onChange={(e) => setVideoDefinition(e.target.value)} required>
          <option value="SD">SD</option>
          <option value="HD">HD</option>
        </select>
      </div>
      <div>
        <label>Start Timecode</label>
        <input type="text" value={startTimecode} onChange={(e) => setStartTimecode(e.target.value)} required placeholder="HH:MM:SS:FF" />
      </div>
      <div>
        <label>End Timecode</label>
        <input type="text" value={endTimecode} onChange={(e) => setEndTimecode(e.target.value)} required placeholder="HH:MM:SS:FF" />
      </div>
      <button type="submit">Add Clip</button>
    </form>
  );
};

export default ClipForm;
