import React, { useState, useEffect } from 'react';
import axios from 'axios';
import VideoClipForm from './VideoClipForm';

const VideoClipList = ({ showReel }) => {
  const [videoClips, setVideoClips] = useState([]);

  useEffect(() => {
    fetchVideoClips();
  }, [showReel]);

  const fetchVideoClips = async () => {
    const response = await axios.get(`/api/v1/show_reels/${showReel.id}/video_clips`);
    setVideoClips(response.data);
  };

  const handleAddClip = (newClip) => {
    setVideoClips([...videoClips, newClip]);
  };

  const handleRemoveClip = async (clipId) => {
    await axios.delete(`/api/v1/show_reels/${showReel.id}/video_clips/${clipId}`);
    setVideoClips(videoClips.filter(clip => clip.id !== clipId));
  };

  return (
    <div>
      <h3>{showReel.name} - Video Clips</h3>
      <VideoClipForm showReelId={showReel.id} onAddClip={handleAddClip} />
      <ul>
        {videoClips.map((clip) => (
          <li key={clip.id}>
            {clip.name} ({clip.start_timecode} - {clip.end_timecode})
            <button onClick={() => handleRemoveClip(clip.id)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default VideoClipList;
