import React, { useState } from 'react';
import axios from 'axios';

const ShowReelForm = ({ onShowReelCreated }) => {
  const [name, setName] = useState('');
  const [videoStandard, setVideoStandard] = useState('PAL');
  const [videoDefinition, setVideoDefinition] = useState('HD');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/show_reels', { name, videoStandard, videoDefinition });
      onShowReelCreated(response.data);
    } catch (error) {
      console.error("There was an error creating the show reel!", error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Name</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
      </div>
      <div>
        <label>Video Standard</label>
        <select value={videoStandard} onChange={(e) => setVideoStandard(e.target.value)} required>
          <option value="PAL">PAL</option>
          <option value="NTSC">NTSC</option>
        </select>
      </div>
      <div>
        <label>Video Definition</label>
        <select value={videoDefinition} onChange={(e) => setVideoDefinition(e.target.value)} required>
          <option value="SD">SD</option>
          <option value="HD">HD</option>
        </select>
      </div>
      <button type="submit">Create Show Reel</button>
    </form>
  );
};

export default ShowReelForm;
